Scripts.OnEnter:
if (self.name) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.name, 1, 1, 1, 1);
	if (self.longDescription) then
		GameTooltip:AddLine(self.longDescription);
	end
	GameTooltip:Show();
end

if self.lockBattlegrounds then GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT") GameTooltip:SetText(BATTLEGROUND_LOCK_TOOLTIP_HEADER)
for _, bgName in pairs(self.lockBattlegrounds) do GameTooltip:AddLine(string.format(BATTLEGROUND_LOCK_TOOLTIP_BGNAME, bgName), HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b, 1) end
GameTooltip:Show() end self.IconHighLight:Show()
