Scripts.OnClick:
local searchOption = self:GetParent();
local editBox = searchOption:GetParent();

editBox:Insert(self.OptionName:GetText());

local filterFrom = self:GetParent();
local editBox = filterFrom:GetParent();

if editBox.textCursorX and editBox.textCursorY then
	editBox:HighlightText(editBox.textCursorX - 1, editBox.textCursorY);
end

editBox:Insert(self.FilterName:GetText());

Scripts.OnMouseDown:
if self:IsEnabled() then
	self.Texture:SetPoint("TOPLEFT", self, "TOPLEFT", 1, -1);
end

Scripts.OnLoad:
self.id = self:GetParent():GetID();

if self.id == 1 then
	self.Price:SetPoint("RIGHT", self.Icon, "LEFT", 0, 0);
end
