Scripts.OnClick:
PlaySound("igCharacterInfoTab");
LFGDebug("Proposal Hidden: Ready Dialog close button pressed.");
StaticPopupSpecial_Hide(LFDDungeonReadyPopup);

Scripts.OnShow:
LFDDungeonReadyPopup_Update();

--Request new lock info (which comes with which rewards we're getting) in case the rewards changed.
RequestLFDPlayerLockInfo();
RequestLFDPartyLockInfo();

Scripts.OnEnter:
if ( self.tooltip ) then
GameTooltip:SetOwner(self, "ANCHOR_TOP");
GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end

Scripts.OnLeave:
if ( GameTooltip:GetOwner() == self ) then
GameTooltip:Hide();
end

Scripts.OnEnter:
if self.name then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.name, nil, nil, nil, nil, true);
end

local itemID = self.itemID;
if itemID then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetHyperlink(string.format("item:%d", itemID));
	CursorUpdate(self);
end

Scripts.OnClick:
local itemLink = self.itemLink;
if itemLink then
	HandleModifiedItemClick(itemLink);
end

local inviteID = self:GetParent().inviteID;
if inviteID then
	PlaySound(SOUNDKIT.IG_CHARACTER_INFO_TAB);
	C_MiniGames.AcceptInvite(inviteID);
end

local inviteID = self:GetParent().inviteID;
if inviteID then
	PlaySound(SOUNDKIT.IG_CHARACTER_INFO_TAB);
	C_MiniGames.DeclineInvite(inviteID);
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
GameTooltip:SetText(GUIDE_TOOLTIP, nil, nil, nil, nil, 1);
LFDFrameRoleCheckButton_OnEnter(self);

Scripts.OnLoad:
local myName = self:GetName();
_G[myName.."ScrollBackground"]:SetParent(_G[myName.."ScrollBar"]);
_G[myName.."ScrollBackgroundTopLeft"]:SetParent(_G[myName.."ScrollBar"]);
_G[myName.."ScrollBackgroundBottomRight"]:SetParent(_G[myName.."ScrollBar"]);

local scrollBar = _G[myName.."ScrollBar"];
scrollBar:ClearAllPoints();
scrollBar:SetPoint("BOTTOMLEFT", self, "BOTTOMRIGHT", 3, 7);
scrollBar:SetPoint("TOPLEFT", self, "TOPRIGHT", 3, -9);

self.scrollBarHideable = true;
ScrollFrame_OnLoad(self);

local myName = self:GetName();
self.description:SetTextColor(1, 1, 1);
self.rewardsDescription:SetTextColor(1, 1, 1);
self.pugDescription:SetTextColor(1, 1, 1);
self.moneyLabel:SetTextColor(1, 1, 1);
self.xpLabel:SetTextColor(1, 1, 1);

Scripts.OnClick:
local mode, subMode = GetLFGMode();
if ( mode == "queued" or mode == "listed" or mode == "rolecheck" ) then
LeaveLFG();
else
LFDQueueFrame_Join();
end

Scripts.OnLoad:
local myName = self:GetName();
_G[myName.."ScrollBackground"]:SetParent(_G[myName.."ScrollBar"]);
_G[myName.."ScrollBackgroundTopLeft"]:SetParent(_G[myName.."ScrollBar"]);
_G[myName.."ScrollBackgroundBottomRight"]:SetParent(_G[myName.."ScrollBar"]);

local scrollBar = _G[myName.."ScrollBar"];
scrollBar:ClearAllPoints();
scrollBar:SetPoint("BOTTOMLEFT", self, "BOTTOMRIGHT", 3, 7);
scrollBar:SetPoint("TOPLEFT", self, "TOPRIGHT", 3, -9);

self.scrollBarHideable = true;
ScrollFrame_OnLoad(self);

local banner = self:GetParent();
self.FlashBurst:ClearAllPoints();
self.FlashBurst:SetPoint("CENTER", banner.SkullSpikes, 15, -4);
self.FlashBurstLeft:ClearAllPoints();
self.FlashBurstLeft:SetPoint("CENTER", banner.SkullSpikes, -15, -4);
self.FlashBurstCenter:ClearAllPoints();
self.FlashBurstCenter:SetPoint("CENTER", banner.SkullSpikes, 0, 0);
self.RedFlash:ClearAllPoints();
self.RedFlash:SetPoint("CENTER", banner.SkullSpikes, 1, -4);
